mapper.insert:
INSERT INTO MEMBER_TABLE
(
	memID
	, memPassword
	, memName
	, memAge
	, memGender
	, memEmail
	, memProfile	
)
VALUES
(
	#{memID}
	, #{memPassword}
	, #{memName}
	, #{memAge}
	, #{memGender}
	, #{memEmail}
	, #{memProfile}
)

mapper.update:
UPDATE MEMBER_TABLE
   SET memPassword=#{memPassword}
   	   , memName=#{memName}
   	   , memAge=#{memAge}
   	   , memGender=#{memGender}
   	   , memEmail=#{memEmail}
   	   , memProfile=#{memProfile}
 WHERE memID=#{memID}

mapper.select:
SELECT memID, memPassword, memName, memAge, memGender, memEmail, memProfile
  FROM MEMBER_TABLE
 WHERE memID=#{memID}

SELECT memProfile
  FROM MEMBER_TABLE
 WHERE memID=#{memID}
